/**
 * ContextGate - bloquea acceso sin contexto activo
 * Muestra selector de liga/equipo si no hay selecci√≥n
 * Solo renderiza children si hay contexto v√°lido
 */
import React, { useState } from 'react';
import { useAppContext } from '@/contexts/ContextProvider';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Skeleton } from '@/components/ui/skeleton';
import { AlertCircle } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';

export function ContextGate({ children }: { children: React.ReactNode }) {
  const { context, loading, error, activeLeague, setActiveContext } = useAppContext();
  const [selectedLeagueKey, setSelectedLeagueKey] = useState<string>('');
  const [selectedTeamKey, setSelectedTeamKey] = useState<string>('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Mientras carga
  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Card className="w-full max-w-md">
          <CardHeader>
            <CardTitle>Cargando contexto...</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <Skeleton className="h-10" />
            <Skeleton className="h-10" />
          </CardContent>
        </Card>
      </div>
    );
  }

  // Error
  if (error) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Card className="w-full max-w-md border-destructive">
          <CardHeader>
            <CardTitle className="text-destructive">Error</CardTitle>
          </CardHeader>
          <CardContent>
            <Alert variant="destructive">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Sin ligas
  if (!context || context.leagues.length === 0) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Card className="w-full max-w-md">
          <CardHeader>
            <CardTitle>Sin ligas disponibles</CardTitle>
            <CardDescription>
              No se encontraron ligas para tu cuenta
            </CardDescription>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-muted-foreground">
              Por favor, verifica tu cuenta de Yahoo Fantasy NBA
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  // Con contexto activo, renderizar children
  if (context.active_league_key && context.active_team_key) {
    return <>{children}</>;
  }

  // Selector de liga/equipo
  const leagues = context.leagues;
  const selectedLeague = leagues.find((l) => l.league_key === selectedLeagueKey);
  const teams = selectedLeague?.teams || [];

  const handleSubmit = async () => {
    if (!selectedLeagueKey || !selectedTeamKey) return;

    setIsSubmitting(true);
    try {
      const success = await setActiveContext(selectedLeagueKey, selectedTeamKey);
      if (!success) {
        alert('Error al cambiar contexto');
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-background to-muted">
      <Card className="w-full max-w-md">
        <CardHeader>
          <CardTitle>Selecciona tu liga y equipo</CardTitle>
          <CardDescription>
            Elige d√≥nde quieres jugar
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Liga */}
          <div className="space-y-2">
            <label className="text-sm font-medium">Liga</label>
            <Select value={selectedLeagueKey} onValueChange={setSelectedLeagueKey}>
              <SelectTrigger>
                <SelectValue placeholder="Selecciona una liga" />
              </SelectTrigger>
              <SelectContent>
                {leagues.map((league) => (
                  <SelectItem key={league.league_key} value={league.league_key}>
                    {league.name} ({league.season})
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Equipo */}
          {selectedLeague && (
            <div className="space-y-2">
              <label className="text-sm font-medium">Equipo</label>
              <Select value={selectedTeamKey} onValueChange={setSelectedTeamKey}>
                <SelectTrigger>
                  <SelectValue placeholder="Selecciona tu equipo" />
                </SelectTrigger>
                <SelectContent>
                  {teams.map((team) => (
                    <SelectItem key={team.team_key} value={team.team_key}>
                      {team.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          )}

          {/* Submit */}
          <Button
            onClick={handleSubmit}
            disabled={!selectedLeagueKey || !selectedTeamKey || isSubmitting}
            className="w-full"
          >
            {isSubmitting ? 'Cargando...' : 'Continuar'}
          </Button>
        </CardContent>
      </Card>
    </div>
  );
}

/**
 * Context Selector - Quick access to change active league/team
 * 
 * Uses ContextProvider to read/update context
 */

import { useAppContext } from "@/contexts/ContextProvider";
import { Button } from "@/components/ui/button";
import { ContextGate } from "./ContextGate";
import { ChevronDown } from "lucide-react";

export function ContextSelector() {
  const context = useAppContext();

  // If no context, show selector button
  if (!context?.activeLeague) {
    return (
      <Button
        variant="outline"
        size="sm"
        className="gap-2"
        onClick={() => {
          // Trigger context gate to show selector
          // This is handled by ContextGate component
        }}
      >
        <span className="hidden sm:inline">Select League</span>
        <ChevronDown className="h-4 w-4" />
      </Button>
    );
  }

  // If context exists, show current selection
  return (
    <Button
      variant="ghost"
      size="sm"
      className="gap-2 text-xs"
      onClick={() => {
        // Could open a menu to change context
        // For now, just show current selection
      }}
    >
      <span className="hidden sm:inline">{context.activeLeague.name}</span>
      {context.activeTeam && (
        <span className="hidden md:inline text-muted-foreground">
          {context.activeTeam.name}
        </span>
      )}
      <ChevronDown className="h-4 w-4" />
    </Button>
  );
}

/**
 * InsiderPanel - Renderiza 4 cards determin√≠sticas
 * - 100% shadcn/ui
 * - Textos desde copy_key
 * - Evidence opcional (debug)
 */
import React, { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Skeleton } from '@/components/ui/skeleton';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { AlertCircle } from 'lucide-react';
import { copy } from '@/lib/copy/es';
import type { InsiderData, Card as InsiderCard } from '@/lib/schemas/insider';

interface InsiderPanelProps {
  data: InsiderData | null;
  loading: boolean;
  error: string | null;
  showEvidence?: boolean;
}

export function InsiderPanel({
  data,
  loading,
  error,
  showEvidence = false,
}: InsiderPanelProps) {
  const [expandedCard, setExpandedCard] = useState<string | null>(null);

  // Funci√≥n helper para interpolar params en copy keys
  const interpolateText = (text: string, params: Record<string, any>): string => {
    let result = text;
    Object.entries(params).forEach(([key, value]) => {
      result = result.replace(`{{${key}}}`, String(value));
    });
    return result;
  };

  // Funci√≥n helper para obtener copy text
  const getCopyText = (key: string, params: Record<string, any> = {}): string => {
    const keys = key.split('.');
    let value: any = copy;

    for (const k of keys) {
      value = value?.[k];
    }

    if (typeof value === 'string') {
      return interpolateText(value, params);
    }

    return key; // Fallback: devolver la key si no existe
  };

  // Funci√≥n para renderizar badge por tipo (usando tokens shadcn)
  const getCardTypeColor = (type: string) => {
    switch (type) {
      case 'positive':
        return 'bg-muted text-foreground';
      case 'negative':
        return 'bg-destructive/10 text-destructive';
      case 'neutral':
        return 'bg-muted text-muted-foreground';
      case 'info':
        return 'bg-muted text-foreground';
      default:
        return 'bg-muted text-muted-foreground';
    }
  };

  if (error) {
    return (
      <Alert variant="destructive">
        <AlertCircle className="h-4 w-4" />
        <AlertDescription>{error}</AlertDescription>
      </Alert>
    );
  }

  if (loading) {
    return (
      <div className="space-y-4">
        {[1, 2, 3, 4].map((i) => (
          <Card key={i}>
            <CardHeader>
              <Skeleton className="h-6 w-1/2" />
            </CardHeader>
            <CardContent className="space-y-2">
              <Skeleton className="h-4 w-full" />
              <Skeleton className="h-4 w-3/4" />
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  if (!data || data.cards.length !== 4) {
    return (
      <Alert>
        <AlertCircle className="h-4 w-4" />
        <AlertDescription>Sin datos de insights disponibles</AlertDescription>
      </Alert>
    );
  }

  return (
    <div className="space-y-4">
      <div className="space-y-2">
        <h2 className="text-2xl font-bold">{copy.insider.title}</h2>
        <p className="text-muted-foreground">{copy.insider.subtitle}</p>
      </div>

      {data.cards.map((card: InsiderCard) => (
        <Card key={card.id}>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div className="flex-1">
                <CardTitle>{getCopyText(card.title_key, card.params)}</CardTitle>
                <CardDescription>
                  {getCopyText(card.body_key, card.params)}
                </CardDescription>
              </div>
              <Badge className={getCardTypeColor(card.type)}>
                {card.type}
              </Badge>
            </div>
          </CardHeader>

          <CardContent className="space-y-4">
            {/* Actions */}
            {card.actions && card.actions.length > 0 && (
              <div className="flex flex-wrap gap-2">
                {card.actions.map((action, idx) => (
                  <Button
                    key={idx}
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      // Aqu√≠ ir√≠a la navegaci√≥n
                      console.log('Action:', action.action);
                    }}
                  >
                    {getCopyText(action.label_key)}
                  </Button>
                ))}
              </div>
            )}

            {/* Evidence (debug) */}
            {showEvidence && card.evidence && card.evidence.length > 0 && (
              <div className="border-t pt-4">
                <button
                  onClick={() =>
                    setExpandedCard(expandedCard === card.id ? null : card.id)
                  }
                  className="text-xs text-muted-foreground hover:underline"
                >
                  {expandedCard === card.id ? '‚ñº' : '‚ñ∂'} {copy.insider.evidence.label}
                </button>

                {expandedCard === card.id && (
                  <div className="mt-2 space-y-2 text-xs">
                    {card.evidence.map((ev, idx) => (
                      <div key={idx} className="bg-muted p-2 rounded">
                        <div>
                          <strong>{copy.insider.evidence.domain}:</strong> {ev.domain}
                        </div>
                        <div>
                          <strong>{copy.insider.evidence.lastSyncAt}:</strong>{' '}
                          {ev.last_sync_at || 'N/A'}
                        </div>
                        {ev.checksum && (
                          <div>
                            <strong>{copy.insider.evidence.checksum}:</strong>{' '}
                            {ev.checksum.substring(0, 8)}...
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </CardContent>
        </Card>
      ))}

      {/* Meta info */}
      {data.meta && (
        <div className="text-xs text-muted-foreground text-center">
          Datos desde {new Date(data.meta.last_sync_at_min).toLocaleString()} hasta{' '}
          {new Date(data.meta.last_sync_at_max).toLocaleString()}
        </div>
      )}
    </div>
  );
}

import { useEffect, useState } from "react";

import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogTitle,
} from "@/components/ui/dialog";

interface ManusDialogProps {
  title?: string;
  logo?: string;
  onLogin: () => void;
  onOpenChange?: (open: boolean) => void;
  onClose?: () => void;
}

export function ManusDialog({
  title,
  logo,
  onLogin,
  onOpenChange,
  onClose,
}: ManusDialogProps) {
  const [internalOpen, setInternalOpen] = useState(false);

  useEffect(() => {
    if (!onOpenChange) {
      // internalOpen is controlled by Dialog's onOpenChange
    }
  }, [onOpenChange]);

  const handleOpenChange = (nextOpen: boolean) => {
    if (onOpenChange) {
      onOpenChange(nextOpen);
    } else {
      setInternalOpen(nextOpen);
    }

    if (!nextOpen) {
      onClose?.();
    }
  };

  return (
    <Dialog
      open={internalOpen}
      onOpenChange={handleOpenChange}
    >
      <DialogContent className="py-5 bg-background rounded-[20px] w-[400px] shadow-sm border border-border backdrop-blur-2xl p-0 gap-0 text-center">
        <div className="flex flex-col items-center gap-2 p-5 pt-12">
          {logo ? (
            <div className="w-16 h-16 bg-muted rounded-xl border border-border flex items-center justify-center">
              <img src={logo} alt="Dialog graphic" className="w-10 h-10 rounded-md" />
            </div>
          ) : null}

          {/* Title and subtitle */}
          {title ? (
            <DialogTitle className="text-xl font-semibold text-foreground leading-[26px] tracking-[-0.44px]">
              {title}
            </DialogTitle>
          ) : null}
          <DialogDescription className="text-sm text-muted-foreground leading-5 tracking-[-0.154px]">
            Please login with Manus to continue
          </DialogDescription>
        </div>

        <DialogFooter className="px-5 py-5">
          {/* Login button */}
          <Button
            onClick={onLogin}
            className="w-full h-10 bg-primary hover:bg-primary/90 text-primary-foreground rounded-[10px] text-sm font-medium leading-5 tracking-[-0.154px]"
          >
            Login with Manus
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

/**
 * GOOGLE MAPS FRONTEND INTEGRATION - ESSENTIAL GUIDE
 *
 * USAGE FROM PARENT COMPONENT:
 * ======
 *
 * const mapRef = useRef<google.maps.Map | null>(null);
 *
 * <MapView
 *   initialCenter={{ lat: 40.7128, lng: -74.0060 }}
 *   initialZoom={15}
 *   onMapReady={(map) => {
 *     mapRef.current = map; // Store to control map from parent anytime, google map itself is in charge of the re-rendering, not react state.
 * </MapView>
 *
 * ======
 * Available Libraries and Core Features:
 * -------------------------------
 * üìç MARKER (from `marker` library)
 * - Attaches to map using { map, position }
 * new google.maps.marker.AdvancedMarkerElement({
 *   map,
 *   position: { lat: 37.7749, lng: -122.4194 },
 *   title: "San Francisco",
 * });
 *
 * -------------------------------
 * üè¢ PLACES (from `places` library)
 * - Does not attach directly to map; use data with your map manually.
 * const place = new google.maps.places.Place({ id: PLACE_ID });
 * await place.fetchFields({ fields: ["displayName", "location"] });
 * map.setCenter(place.location);
 * new google.maps.marker.AdvancedMarkerElement({ map, position: place.location });
 *
 * -------------------------------
 * üß≠ GEOCODER (from `geocoding` library)
 * - Standalone service; manually apply results to map.
 * const geocoder = new google.maps.Geocoder();
 * geocoder.geocode({ address: "New York" }, (results, status) => {
 *   if (status === "OK" && results[0]) {
 *     map.setCenter(results[0].geometry.location);
 *     new google.maps.marker.AdvancedMarkerElement({
 *       map,
 *       position: results[0].geometry.location,
 *     });
 *   }
 * });
 *
 * -------------------------------
 * üìê GEOMETRY (from `geometry` library)
 * - Pure utility functions; not attached to map.
 * const dist = google.maps.geometry.spherical.computeDistanceBetween(p1, p2);
 *
 * -------------------------------
 * üõ£Ô∏è ROUTES (from `routes` library)
 * - Combines DirectionsService (standalone) + DirectionsRenderer (map-attached)
 * const directionsService = new google.maps.DirectionsService();
 * const directionsRenderer = new google.maps.DirectionsRenderer({ map });
 * directionsService.route(
 *   { origin, destination, travelMode: "DRIVING" },
 *   (res, status) => status === "OK" && directionsRenderer.setDirections(res)
 * );
 *
 * -------------------------------
 * üå¶Ô∏è MAP LAYERS (attach directly to map)
 * - new google.maps.TrafficLayer().setMap(map);
 * - new google.maps.TransitLayer().setMap(map);
 * - new google.maps.BicyclingLayer().setMap(map);
 *
 * -------------------------------
 * ‚úÖ SUMMARY
 * - ‚Äúmap-attached‚Äù ‚Üí AdvancedMarkerElement, DirectionsRenderer, Layers.
 * - ‚Äústandalone‚Äù ‚Üí Geocoder, DirectionsService, DistanceMatrixService, ElevationService.
 * - ‚Äúdata-only‚Äù ‚Üí Place, Geometry utilities.
 */

/// <reference types="@types/google.maps" />

import { useEffect, useRef } from "react";
import { usePersistFn } from "@/hooks/usePersistFn";
import { cn } from "@/lib/utils";

declare global {
  interface Window {
    google?: typeof google;
  }
}

const API_KEY = import.meta.env.VITE_FRONTEND_FORGE_API_KEY;
const FORGE_BASE_URL =
  import.meta.env.VITE_FRONTEND_FORGE_API_URL ||
  "https://forge.butterfly-effect.dev";
const MAPS_PROXY_URL = `${FORGE_BASE_URL}/v1/maps/proxy`;

function loadMapScript() {
  return new Promise(resolve => {
    const script = document.createElement("script");
    script.src = `${MAPS_PROXY_URL}/maps/api/js?key=${API_KEY}&v=weekly&libraries=marker,places,geocoding,geometry`;
    script.async = true;
    script.crossOrigin = "anonymous";
    script.onload = () => {
      resolve(null);
      script.remove(); // Clean up immediately
    };
    script.onerror = () => {
      console.error("Failed to load Google Maps script");
    };
    document.head.appendChild(script);
  });
}

interface MapViewProps {
  className?: string;
  initialCenter?: google.maps.LatLngLiteral;
  initialZoom?: number;
  onMapReady?: (map: google.maps.Map) => void;
}

export function MapView({
  className,
  initialCenter = { lat: 37.7749, lng: -122.4194 },
  initialZoom = 12,
  onMapReady,
}: MapViewProps) {
  const mapContainer = useRef<HTMLDivElement>(null);

  const init = usePersistFn(async () => {
    await loadMapScript();
    if (!mapContainer.current) {
      console.error("Map container not found");
      return;
    }
    const mapInstance = new window.google.maps.Map(mapContainer.current, {
      zoom: initialZoom,
      center: initialCenter,
      mapTypeControl: true,
      fullscreenControl: true,
      zoomControl: true,
      streetViewControl: true,
      mapId: "DEMO_MAP_ID",
    });
    if (onMapReady) {
      onMapReady(mapInstance);
    }
  });

  useEffect(() => {
    init();
  }, [init]);

  return (
    <div ref={mapContainer} className={cn("w-full h-[500px]", className)} />
  );
}

