import { useDialogComposition } from "@/components/ui/dialog";
import { useComposition } from "@/hooks/useComposition";
import { cn } from "@/lib/utils";
import * as React from "react";

function Textarea({
  className,
  onKeyDown,
  onCompositionStart,
  onCompositionEnd,
  ...props
}: React.ComponentProps<"textarea">) {
  // Get dialog composition context if available (will be no-op if not inside Dialog)
  const dialogComposition = useDialogComposition();

  // Add composition event handlers to support input method editor (IME) for CJK languages.
  const {
    onCompositionStart: handleCompositionStart,
    onCompositionEnd: handleCompositionEnd,
    onKeyDown: handleKeyDown,
  } = useComposition<HTMLTextAreaElement>({
    onKeyDown: (e) => {
      // Check if this is an Enter key that should be blocked
      const isComposing = (e.nativeEvent as any).isComposing || dialogComposition.justEndedComposing();

      // If Enter key is pressed while composing or just after composition ended,
      // don't call the user's onKeyDown (this blocks the business logic)
      // Note: For textarea, Shift+Enter should still work for newlines
      if (e.key === "Enter" && !e.shiftKey && isComposing) {
        return;
      }

      // Otherwise, call the user's onKeyDown
      onKeyDown?.(e);
    },
    onCompositionStart: e => {
      dialogComposition.setComposing(true);
      onCompositionStart?.(e);
    },
    onCompositionEnd: e => {
      // Mark that composition just ended - this helps handle the Enter key that confirms input
      dialogComposition.markCompositionEnd();
      // Delay setting composing to false to handle Safari's event order
      // In Safari, compositionEnd fires before the ESC keydown event
      setTimeout(() => {
        dialogComposition.setComposing(false);
      }, 100);
      onCompositionEnd?.(e);
    },
  });

  return (
    <textarea
      data-slot="textarea"
      className={cn(
        "border-input placeholder:text-muted-foreground focus-visible:border-ring focus-visible:ring-ring/50 aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive dark:bg-input/30 flex field-sizing-content min-h-16 w-full rounded-md border bg-transparent px-3 py-2 text-base shadow-xs transition-[color,box-shadow] outline-none focus-visible:ring-[3px] disabled:cursor-not-allowed disabled:opacity-50 md:text-sm",
        className
      )}
      onCompositionStart={handleCompositionStart}
      onCompositionEnd={handleCompositionEnd}
      onKeyDown={handleKeyDown}
      {...props}
    />
  );
}

export { Textarea };

/**
 * ContextProvider - proporciona contexto del usuario a toda la app
 * Lee /v1/context desde API
 * Maneja selecci√≥n activa de liga/equipo
 */
import React, { createContext, useContext, useEffect, useState } from 'react';
import { useContext as useContextHook } from '@/hooks/useContext';
import type { Context, League, Team } from '@/hooks/useContext';

interface ContextContextType {
  ownerId: string | null;
  context: Context | null;
  loading: boolean;
  error: string | null;
  activeLeague: League | null;
  activeTeam: Team | null;
  setActiveContext: (_leagueKey: string, _teamKey?: string) => Promise<boolean>;
}

const ContextContext = createContext<ContextContextType | undefined>(undefined);

export function ContextProvider({
  children,
  ownerId,
}: {
  children: React.ReactNode;
  ownerId: string;
}) {
  const { context, loading, error, setActiveContext } = useContextHook(ownerId);
  const [activeLeague, setActiveLeague] = useState<League | null>(null);
  const [activeTeam, setActiveTeam] = useState<Team | null>(null);

  // Actualizar liga/equipo activos cuando cambia el contexto
  useEffect(() => {
    if (context) {
      const league = context.leagues.find(
        (l) => l.league_key === context.active_league_key
      );
      setActiveLeague(league || null);

      if (league && context.active_team_key) {
        const team = league.teams.find((t) => t.team_key === context.active_team_key);
        setActiveTeam(team || null);
      } else {
        setActiveTeam(null);
      }
    } else {
      setActiveLeague(null);
      setActiveTeam(null);
    }
  }, [context]);

  const value: ContextContextType = {
    ownerId,
    context,
    loading,
    error,
    activeLeague,
    activeTeam,
    setActiveContext,
  };

  return (
    <ContextContext.Provider value={value}>{children}</ContextContext.Provider>
  );
}

export function useAppContext() {
  const context = useContext(ContextContext);
  if (!context) {
    throw new Error('useAppContext must be used within ContextProvider');
  }
  return context;
}

/**
 * Theme Provider - Manages base colors and accent themes
 * 
 * Separate from next-themes (which handles light/dark mode)
 * Applies classes to <html> and persists to localStorage
 * 
 * Storage contract:
 * - ui.base: "base-zinc" | "base-slate" | "base-stone" | "base-gray" | "base-neutral"
 * - ui.accent: "theme-default" | "theme-blue" | ... | "theme-yellow"
 */

import React, { createContext, useContext, useEffect, useState } from "react";
import { BASE_COLORS, ACCENT_THEMES, type BaseColor, type AccentTheme } from "@/config/themes";

interface ThemeContextType {
  base: BaseColor;
  accent: AccentTheme;
  setBase: (_base: BaseColor) => void;
  setAccent: (_accent: AccentTheme) => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

const DEFAULT_BASE: BaseColor = "zinc";
const DEFAULT_ACCENT: AccentTheme = "default";

function getStorageBase(): BaseColor {
  if (typeof window === "undefined") return DEFAULT_BASE;

  const stored = localStorage.getItem("ui.base");
  if (stored && BASE_COLORS.includes(stored as BaseColor)) {
    return stored as BaseColor;
  }

  // Fallback: reset to default
  localStorage.setItem("ui.base", `base-${DEFAULT_BASE}`);
  return DEFAULT_BASE;
}

function getStorageAccent(): AccentTheme {
  if (typeof window === "undefined") return DEFAULT_ACCENT;

  const stored = localStorage.getItem("ui.accent");
  if (stored && ACCENT_THEMES.includes(stored as AccentTheme)) {
    return stored as AccentTheme;
  }

  // Fallback: reset to default
  localStorage.setItem("ui.accent", `theme-${DEFAULT_ACCENT}`);
  return DEFAULT_ACCENT;
}

function applyThemeClasses(base: BaseColor, accent: AccentTheme) {
  if (typeof document === "undefined") return;

  const html = document.documentElement;

  // Remove all base-* classes
  BASE_COLORS.forEach((b) => {
    html.classList.remove(`base-${b}`);
  });

  // Remove all theme-* classes
  ACCENT_THEMES.forEach((a) => {
    html.classList.remove(`theme-${a}`);
  });

  // Apply current base (only if not default)
  if (base !== DEFAULT_BASE) {
    html.classList.add(`base-${base}`);
  }

  // Apply current accent (only if not default)
  if (accent !== DEFAULT_ACCENT) {
    html.classList.add(`theme-${accent}`);
  }
}

export function ThemeProvider({ children }: { children: React.ReactNode }) {
  const [base, setBaseState] = useState<BaseColor>(DEFAULT_BASE);
  const [accent, setAccentState] = useState<AccentTheme>(DEFAULT_ACCENT);
  const [mounted, setMounted] = useState(false);

  // Hydrate from storage on mount
  useEffect(() => {
    const storedBase = getStorageBase();
    const storedAccent = getStorageAccent();

    setBaseState(storedBase);
    setAccentState(storedAccent);
    applyThemeClasses(storedBase, storedAccent);
    setMounted(true);
  }, []);

  const setBase = (newBase: BaseColor) => {
    setBaseState(newBase);
    localStorage.setItem("ui.base", `base-${newBase}`);
    applyThemeClasses(newBase, accent);
  };

  const setAccent = (newAccent: AccentTheme) => {
    setAccentState(newAccent);
    localStorage.setItem("ui.accent", `theme-${newAccent}`);
    applyThemeClasses(base, newAccent);
  };

  // Prevent hydration mismatch
  if (!mounted) {
    return <>{children}</>;
  }

  return (
    <ThemeContext.Provider value={{ base, accent, setBase, setAccent }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error("useTheme must be used within ThemeProvider");
  }
  return context;
}

import { useState, useEffect } from "react"
import { z } from "zod"
import { createAPIResponseSchema } from "@/lib/zod-schemas"

interface UseAPIResponseResult<T> {
  data: T | null
  isLoading: boolean
  error: Array<{ code: string; message: string; field?: string }> | null
  isStale: boolean
  lastSyncAt: string | null
  capabilities: any | null
}

export function useAPIResponse<T extends z.ZodTypeAny>(
  url: string,
  dataSchema: T,
  options?: { refetchInterval?: number }
): UseAPIResponseResult<z.infer<T>> {
  const [state, setState] = useState<UseAPIResponseResult<z.infer<T>>>({
    data: null,
    isLoading: true,
    error: null,
    isStale: false,
    lastSyncAt: null,
    capabilities: null,
  })

  useEffect(() => {
    let isMounted = true
    const controller = new AbortController()

    const fetchData = async () => {
      try {
        const response = await fetch(url, { signal: controller.signal })
        const json = await response.json()

        // Validate against API Envelope schema
        const envelopeSchema = createAPIResponseSchema(dataSchema)
        const validated = envelopeSchema.parse(json)

        if (isMounted) {
          setState({
            data: validated.data || null,
            isLoading: false,
            error: validated.errors || null,
            isStale: Object.values(validated.meta.last_sync_at).some(
              (s) => s.status === "stale"
            ),
            lastSyncAt: validated.meta.snapshot_date,
            capabilities: validated.capabilities,
          })
        }
      } catch (err) {
        if (isMounted) {
          setState((prev) => ({
            ...prev,
            isLoading: false,
            error: [
              {
                code: "VALIDATION_ERROR",
                message: err instanceof Error ? err.message : "Unknown error",
              },
            ],
          }))
        }
      }
    }

    fetchData()

    // Setup refetch interval if provided
    let interval: NodeJS.Timeout | null = null
    if (options?.refetchInterval) {
      interval = setInterval(fetchData, options.refetchInterval)
    }

    return () => {
      isMounted = false
      controller.abort()
      if (interval) clearInterval(interval)
    }
  }, [url, dataSchema, options?.refetchInterval])

  return state
}

import { useRef } from "react";

type noop = (..._args: any[]) => any;

/**
 * usePersistFn instead of useCallback to reduce cognitive load
 */
export function usePersistFn<T extends noop>(fn: T) {
  const fnRef = useRef<T>(fn);
  fnRef.current = fn;

  const persistFn = useRef<T>(null);
  if (!persistFn.current) {
    persistFn.current = function (this: unknown, ...args) {
      return fnRef.current!.apply(this, args);
    } as T;
  }

  return persistFn.current!;
}

