function ThemeScope({
  base,
  accent,
  mode,
  children,
}: {
  base: string;
  accent: string;
  mode: Mode;
  children: React.ReactNode;
}) {
  const themeClasses = getThemeClasses({
    base: base as any,
    accent: accent as any,
    mode,
